package com.examples;

public class EsempioIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -1;
		
		if (a<10) {
			System.out.println("a<10");
		}else if(a==11) {
			System.out.println("a = 11");
		}else if(a == 12) {
			System.out.println("a = 12");
		}else {
			System.out.println("a = " + a);
		} 
	}

}
